mapper.select:
SELECT `index` AS `index`, `board_code` AS `boardCode`, `user_email` AS `userEmail`, `title` AS `title`, `content` AS `content`, `created_at` AS `createdAt`, `view` AS `view` FROM `dcs`.`articles` WHERE BINARY `board_code` = #{code} AND
AND (REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%') OR REPLACE(`content`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%'))
AND REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%')
AND BINARY `user_email` = #{keyword}
ORDER BY `index` DESC LIMIT #{countPerPage} OFFSET #{offset}

SELECT COUNT(0) FROM `dcs`.`articles` WHERE BINARY `board_code` = #{code}
AND (REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%') OR REPLACE(`content`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%'))
AND REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword}, ' ', ''), '%')
AND BINARY `user_email` = #{keyword}
