mapper.select:
SELECT  `index` AS `index`, user_email AS `userEmail`, check_in AS `checkIn`
FROM `dcs`.`attendance`
WHERE `attendance`.`user_email` = #{email}
  AND `attendance`.`check_in` BETWEEN #{firstDate} AND #{secondDate}
LIMIT 1

SELECT  IFNULL(true, false) AS `Boolean`
FROM `dcs`.`attendance`
WHERE `attendance`.`user_email` = #{email}
  AND `attendance`.`check_in` BETWEEN #{firstDate} AND #{secondDate}
LIMIT 1
